import axios from "axios";
import PropTypes from "prop-types";
import { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import { useNavigate, useParams } from "react-router-dom";

export default function ApiDetail({ apiId, onClose }) {
  const { id: routeId } = useParams();
  const id = apiId || routeId;
  const [api, setApi] = useState(null); 
  const [error, setError] = useState(null);
  const apiUrl = process.env.REACT_APP_API_URL;
  const navigate = useNavigate();
  const { t } = useTranslation();

  useEffect(() => {
    axios
      .get(`${apiUrl}/apis/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      })
      .then((res) => setApi(res.data))
      .catch((err) => {
        if (err.response && err.response.status === 401) {
          setError(t('sessionExpired') || "Sesi贸n expirada. Por favor, vuelve a iniciar sesi贸n.");
          setTimeout(() => {
            navigate("/login");
          }, 2000);
        } else {
          setError(t('unexpectedError') || "Error inesperado");
        }
      });
  }, [id, apiUrl, t, navigate]);

  if (error) return <div className="p-6 text-red-600">{error}</div>;
  if (!api) return <div className="p-6">{t('loading')}</div>;

  return (
    <div className="p-6 max-w-3xl mx-auto bg-white rounded-xl shadow relative">
      {onClose && (
        <button
          className="absolute top-2 right-2 text-gray-500 hover:text-gray-700 text-2xl"
          onClick={onClose}
          aria-label={t('close') || "Cerrar"}
        >
          &times;
        </button>
      )}


      <h2 className="text-2xl font-bold mb-2">{api.name}</h2>
      <p className="text-sm text-gray-500 mb-2">Version: {api.version} - Status: {api.status}</p>
      <p className="mb-4">{api.description}</p>
      <div className="border-t pt-4">
        <h3 className="font-semibold mb-2">{t('detailsApiDetails')}</h3>
        <p>{api.detail}</p>
      </div>
    </div>
  );
}

ApiDetail.propTypes = {
  apiId: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
  onClose: PropTypes.func
};
