import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";



export default function ApiList() {
  const [apis, setApis] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const apiUrl = process.env.REACT_APP_API_URL;

  const letters = ["#", ..."ABCDEFGHIJKLMNOPQRSTUVWXYZ".split("")];
  //const [selectedLetter, setSelectedLetter] = useState("#");

  useEffect(() => {
    axios
      .get(`${apiUrl}/apis`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })
      .then((res) => {
        setApis(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setLoading(false);
      });
  }, []);

  const filteredApis = apis.filter(
    (api) =>
      api.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      api.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="api-directory-container p-4">
      <h1 className="text-2xl font-bold mb-4">API Directory</h1>
      <p className="mb-6 text-gray-600">
        Browse and discover available APIs in the TM Forum Open Digital Architecture 
      </p>

 <div className="reflection flex gap-2 flex-wrap justify-center max-w-6xl mx-auto py-6">
      {letters.map((letter) => (
        <button
          key={letter}
          onClick={() => onSelectLetter(letter)}
          className="w-10 h-10 flex items-center justify-center rounded-full border border-gray-300 text-gray-600 hover:text-white hover:bg-indigo-500 transition-all duration-300 font-semibold shadow-[0_0_6px_rgba(99,102,241,0.6)]"
        >
          {letter}
        </button>
      ))}
    </div>




      <div className="search-filter-container mb-6">
        <input
          type="text"
          placeholder="Search APIs..."
          className="search-bar p-2 border rounded w-full md:w-1/2"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <div className="flex justify-between items-center mt-2">
          <span className="text-sm text-gray-500">
            {filteredApis.length} APIs found
          </span>
          <button className="sort-button px-3 py-1 bg-gray-200 rounded text-sm">
            Sort
          </button>
        </div>
      </div>

      {loading ? (
        <div className="loader text-center py-8">Loading...</div>
      ) : filteredApis.length === 0 ? (
        <div className="no-results text-center py-8">
          <h3 className="text-lg font-medium">No APIs found</h3>
          <p className="text-gray-500">
            {searchTerm
              ? "Try adjusting your search query."
              : "There are currently no APIs available."}
          </p>
        </div>
      ) : (
        <div className="api-list-container overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Name
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Version
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Description
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredApis.map((api) => (
                <tr key={api.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap">{api.name}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{api.version}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span
                      className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                        api.status === "Active"
                          ? "bg-green-100 text-green-800"
                          : "bg-yellow-100 text-yellow-800"
                      }`}
                    >
                      {api.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-normal max-w-xs">
                    {api.description}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <Link
                      to={`/apis/${api.id}`}
                      className="text-blue-600 hover:text-blue-900"
                    >
                      View Details
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
