import axios from "axios";
import { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import { useNavigate, useParams } from "react-router-dom";
import { Card, CardContent } from "./ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "./ui/tabs";

export default function ApiDetail({ apiId, onClose }) {
  const { id: routeId } = useParams();
  const [versions, setVersions] = useState([]);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState(0);
  const apiUrl = process.env.REACT_APP_API_URL;
  const navigate = useNavigate();
  const { t } = useTranslation();

  useEffect(() => {
    axios
      .get(`${apiUrl}/api-versions`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      })
      .then((res) => setVersions(res.data))
      .catch((err) => {
        if (err.response && err.response.status === 401) {
          setError(t('sessionExpired') || "Sesión expirada. Por favor, vuelve a iniciar sesión.");
          setTimeout(() => {
            navigate("/login");
          }, 2000);
        } else {
          setError(t('unexpectedError') || "Error inesperado");
        }
      });
  }, [apiUrl, t, navigate]);

  if (error) return <div className="p-6 text-red-600">{error}</div>;
  if (versions.length === 0) {
    return <div className="p-6">{t('noVersions') || "No hay versiones disponibles."}</div>;
  }

  return (
    <div className="p-6 w-full max-w-4xl mx-auto bg-white rounded-xl shadow relative">
      {onClose && (
        <button
          className="absolute top-2 right-2 text-gray-500 hover:text-gray-700 text-2xl"
          onClick={onClose}
          aria-label={t('close') || "Cerrar"}
        >
          &times;
        </button>
      )}

      <Tabs value={String(activeTab)}>
        <TabsList>
          {versions.map((version, idx) => (
            <TabsTrigger
              key={idx}
              value={String(idx)}
              activeValue={String(activeTab)}
              onClick={() => setActiveTab(idx)}
            >
              {version.thisVersion}
            </TabsTrigger>
          ))}
        </TabsList>

        {versions.map((version, idx) => (
          <TabsContent
            key={idx}
            value={String(idx)}
            activeValue={String(activeTab)}
            className="grid grid-cols-1 lg:grid-cols-3 gap-4 mt-4"
          >
            <div className="lg:col-span-2">
              <Card>
                <CardContent>
                  <h2 className="text-xl font-semibold mb-2">{t('overview') || "Overview"}</h2>
                  <p className="text-sm text-gray-700 mb-4">{version.overview}</p>
                  <h3 className="font-semibold">{t('versionHistory') || "Version history"}</h3>
                  <div className="mt-2 text-sm break-words">
                    {version.versionHistory}
                  </div>
                </CardContent>
              </Card>
            </div>
            <Card>
  <CardContent className="p-4 text-sm">
    <div className="space-y-2">
      <div className="flex justify-between">
        <span className="font-semibold text-gray-700">API NO.:</span>
        <span className="text-indigo-700 font-bold">{version.apiNumber || "TMF646"}</span>
      </div>
      <div className="flex justify-between">
        <span className="font-semibold text-gray-700">STABLE VERSION:</span>
        <span>{version.stableVersion || "v4.0"}</span>
      </div>
      <div className="flex justify-between">
        <span className="font-semibold text-gray-700">THIS VERSION:</span>
        <span>{version.thisVersion || "v4.0"}</span>
      </div>
      <div className="flex justify-between">
        <span className="font-semibold text-gray-700">TYPES:</span>
        <span>{version.type || "RESTful"}</span>
      </div>
      <div className="flex justify-between">
        <span className="font-semibold text-gray-700">DOMAIN:</span>
        <span>{version.domain || "Customer"}</span>
      </div>
      <div className="flex justify-between">
        <span className="font-semibold text-gray-700">IPR MODE:</span>
        <span>{version.iprMode || "Apache 2.0 unless stated"}</span>
      </div>
      <div className="flex justify-between">
        <span className="font-semibold text-gray-700">RELEASE DATE:</span>
        <span>{version.releaseDate || "01 Jul 2021"}</span>
      </div>
    </div>
  </CardContent>
</Card>
          </TabsContent>
        ))}
      </Tabs>
    </div>
  );
}
