import axios from "axios";
import { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import { useNavigate, useParams } from "react-router-dom";

export default function ApiDetail({ apiId, onClose }) {
  const { id: routeId } = useParams();
  const [versions, setVersions] = useState([]);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState(0);
  const apiUrl = process.env.REACT_APP_API_URL;
  const navigate = useNavigate();
  const { t } = useTranslation();

  const safeTab = Math.min(activeTab, versions.length - 1);

  useEffect(() => {
    axios
      .get(`${apiUrl}/api-versions`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      })
      .then((res) => setVersions(res.data))
      .catch((err) => {
        if (err.response && err.response.status === 401) {
          setError(t('sessionExpired') || "Sesión expirada. Por favor, vuelve a iniciar sesión.");
          setTimeout(() => {
            navigate("/login");
          }, 2000);
        } else {
          setError(t('unexpectedError') || "Error inesperado");
        }
      });
  }, [apiUrl, t, navigate]);

  useEffect(() => {
    console.log("Valor de versions:", versions);
  }, [versions]);

  if (error) return <div className="p-6 text-red-600">{error}</div>;
  if (versions.length === 0) {
    return <div className="p-6">{t('noVersions') || "No hay versiones disponibles."}</div>;
  }

  const activeVersion = versions[safeTab] || versions[0];

  return (
    <div className="p-6 w-full max-w-3xl mx-auto bg-white rounded-xl shadow relative overflow-x-auto">
  {onClose && (
    <button
      className="absolute top-2 right-2 text-gray-500 hover:text-gray-700 text-2xl"
      onClick={onClose}
      aria-label={t('close') || "Cerrar"}
    >
      &times;
    </button>
  )}
  {/* Pestañas de versiones */}
  <div className="mb-4 border-b border-gray-200 flex overflow-x-auto scrollbar-thin scrollbar-thumb-indigo-200" role="tablist">
    {versions.map((version, idx) => (
      <button
        key={idx}
        role="tab"
        aria-selected={activeTab === idx}
        className={`px-4 py-2 -mb-px font-semibold border-b-2 transition-colors whitespace-nowrap ${
          activeTab === idx
            ? "border-indigo-600 text-indigo-600"
            : "border-transparent text-gray-500 hover:text-indigo-600"
        }`}
        onClick={() => setActiveTab(idx)}
      >
        {version.thisVersion}
      </button>
    ))}
  </div>
  {/* Contenido de la versión activa */}
  <div className="py-2 min-h-[60px]">
  <div className="flex flex-col md:flex-row gap-4 items-start">
    {/* Contenido principal de la versión activa */}
    <div className="flex-1 min-w-0 max-h-[60vh] overflow-auto">
      <p className="text-sm text-gray-500 mb-2">
        {t('stableVersion')}: {activeVersion.stableVersion}
      </p>
      <p className="mb-4 break-words">{activeVersion.overview}</p>
      <div className="border-t pt-4">
        <h3 className="font-semibold mb-2">{t('versionHistory')}</h3>
        <p className="break-words">{activeVersion.versionHistory}</p>
      </div>
    </div>
    {/* Recuadro lateral de información */}
    <div className="bg-white shadow-lg rounded-lg p-4 mb-4 border border-indigo-100 w-full md:w-80 flex-shrink-0 self-start">
      <div className="mb-2 flex justify-between">
        <span className="font-semibold text-gray-700">API No.</span>
        <span className="text-indigo-700 font-bold">{activeVersion.apiNumber || "TMF646"}</span>
      </div>
      <div className="mb-2 flex justify-between">
        <span className="font-semibold text-gray-700">Stable version</span>
        <span>{activeVersion.stableVersion || "v4.0"}</span>
      </div>
      <div className="mb-2 flex justify-between">
        <span className="font-semibold text-gray-700">This Version</span>
        <span>{activeVersion.thisVersion || "v4.0"}</span>
      </div>
      <div className="mb-2 flex justify-between">
        <span className="font-semibold text-gray-700">Types</span>
        <span>{activeVersion.type || "RESTful"}</span>
      </div>
      <div className="mb-2 flex justify-between">
        <span className="font-semibold text-gray-700">DOMAIN</span>
        <span>{activeVersion.domain || "Customer"}</span>
      </div>
      <div className="mb-2 flex justify-between">
        <span className="font-semibold text-gray-700">IPR MODE</span>
        <span>{activeVersion.iprMode || "Apache 2.0 unless stated"}</span>
      </div>
      <div className="flex justify-between">
        <span className="font-semibold text-gray-700">Release Date</span>
        <span>{activeVersion.releaseDate || "01 Jul 2021"}</span>
      </div>
    </div>
  </div>
</div>
</div>
 );
}
